// Role.js

import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import styles from "../styles/Username.module.css";
import { Toaster } from "react-hot-toast";
import { useFormik } from "formik";
import { usernameValidate } from "../helper/validate";
import { useAuthStore } from "../store/store";
import NavBarWithLogout from "./NavBarWithLogout"; // Import NavBar component

export default function Role() {
  const navigate = useNavigate();
  const [roles, setRoles] = useState([
    { id: 1, name: "Admin", status: "Active" },
    { id: 2, name: "Superadmin", status: "Inactive" },
    { id: 3, name: "Caller", status: "Active" },
    { id: 4, name: "Account", status: "Inactive" },
  ]);

  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleEdit = (id) => {
    // Handle edit logic
  };

  const handleDelete = (id) => {
    setRoles(roles.filter(role => role.id !== id));
  };

  const handleAddRole = () => {
    // Handle add role logic
  };

  const formik = useFormik({
    initialValues: {
      username: "",
      password: "",
    },
    validate: usernameValidate,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      useAuthStore.getState().setUsername(values.username);
      navigate("/password");
    },
  });

  const filteredRoles = roles.filter(role => 
    role.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto">
      <Toaster position="top-center" reverseOrder={false}></Toaster>
      <NavBarWithLogout /> {/* Render the NavBar component */}
      <div className={styles.flexContainer}>
        <div className={styles.glass2}>
          <ul>
            <li>
              <Link to="/" className={styles.link}>
                HOME
                <span className={styles.playButton}>▶</span>
              </Link>
            </li>
            <li>
              <Link to="/roles" className={styles.link}>
                ROLES
                <span className={styles.playButton}>▶</span>
              </Link>
            </li>
            <li>
              <Link to="/users" className={styles.link}>
                USERS
                <span className={styles.playButton}>▶</span>
              </Link>
            </li>
          </ul>
        </div>
        <div className={styles.glass3}>
          {/* Add content for the role management here */}
          <div className={styles.roleManagement}>
            <div className={styles.searchBarContainer}>
              <input 
                type="text" 
                placeholder="Search roles..." 
                value={searchTerm}
                onChange={handleSearch}
                className={styles.searchBar}
              />
              <button className={styles.addButton} onClick={handleAddRole}>Add Role</button>
            </div>
            <table className={styles.roleTable}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Role Name</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {filteredRoles.map(role => (
                  <tr key={role.id}>
                    <td>{role.id}</td>
                    <td>{role.name}</td>
                    <td>{role.status}</td>
                    <td>
                      <button className={styles.editButton} onClick={() => handleEdit(role.id)}>Edit</button>
                      <button className={styles.deleteButton} onClick={() => handleDelete(role.id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
