import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import avatar from '../assets/profile.png';
import toast, { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import { registerValidation } from '../helper/validate';
import convertToBase64 from '../helper/convert';
import { registerUser } from '../helper/helper';
import styles from '../styles/Username.module.css';

export default function Register() {
  const navigate = useNavigate();
  const [file, setFile] = useState(null); // Initialize file state with null

  const formik = useFormik({
    initialValues: {
      email: 'admin123@gmail.com',
      username: 'example123',
      password: 'admin@123',
      profile: '', // Initialize profile field
    },
    validate: registerValidation,
    onSubmit: async (values) => {
      try {
        // Check if a file is selected and set it in values.profile
        if (file) {
          values.profile = file;
        }

        // Call the registerUser function
        const registerPromise = registerUser(values);

        // Display loading toast while waiting for the promise
        toast.promise(registerPromise, {
          loading: 'Creating...',
          success: <b>Register Successfully...!</b>,
          error: <b>Could not Register.</b>,
        });

        // Wait for the promise to resolve and then navigate on success
        await registerPromise;
        navigate('/');
      } catch (error) {
        // Handle any errors here (e.g., display an error toast)
        console.error('Registration error:', error);
        toast.error('Registration failed');
      }
    },
  });

/** Formil doesnt support file upload so we need to create this handler */
const onUpload = async e =>{
  const base64 = await convertToBase64(e.target.files[0]);
  setFile(base64);
}

  return (
    <div className="container mx-auto">

      <Toaster position='top-center' reverseOrder={false}></Toaster>

      <div className="flex justify-center items-center h-screen">
        <div className={styles.glass} style={{ width:"40%", paddingTop:'3em'}}>

          <div className="title flex flex-col items-center">
            <h4 className="text-4xl font-bold text-blue-800">Register</h4>
            <span className="py-4 text-xl w-2/3 text-center text-gray-800">
              Happy to join you!
            </span>
          </div>

          <form className='py-1' onSubmit={formik.handleSubmit}>
            <div className="profile flex justify-center py-4">
              <label htmlFor="profile">
              <img src={file || avatar} className={styles.profile_img} alt="avatar" />
              </label>
              <input onChange={onUpload} type="file" id='profile' name="profile"/>
            </div>

            <div className="textbox flex flex-col items-center gap-6">
              <input {...formik.getFieldProps('email')} className={styles.textbox} type="text" placeholder='Email*'/>
              <input {...formik.getFieldProps('username')} className={styles.textbox} type="text" placeholder='Username*'/>
              <input {...formik.getFieldProps('password')} className={styles.textbox} type="text" placeholder='Password*'/>
              <button className={styles.btn} type="submit">Register</button>
            </div>

            <div className="text-center py-4">
              <span className="text-gray-1200">
                Already Register? <Link className='text-red-500' to="/">Login Now</Link>
              </span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
