// NavBar.js

import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import avatar from "../assets/group.png";
import avatar1 from "../assets/image.png";

import styles from '../styles/Username.module.css';
import LogoutDialog from './LogoutDialog'; // Import LogoutDialog component

function NavBar() {
  const [showDialog, setShowDialog] = useState(false);
  const navigate = useNavigate();

  const handleLogoutClick = () => {
    setShowDialog(true);
  };

  const handleConfirmLogout = () => {
    setShowDialog(false);
    // Perform logout logic here, e.g., clearing authentication tokens, etc.
    navigate('/login'); // Redirect to login page or home after logout
  };

  const handleCancelLogout = () => {
    setShowDialog(false);
  };

  return (
    <nav className="bg-purple-800 p-4">
      <div className="container mx-auto flex justify-between items-right">
          <div className="profile flex justify-center py-4">
            <img src={avatar} className={styles.group_img} alt="avatar" />
          </div>
        
        <div>
          <button
            className="text-gray-300 mx-4"
            onClick={handleLogoutClick}
          >
            <Link to="/" className="text-white font-bold text-xl">
          <div className="profile flex justify-right py-4">
            <img src={avatar1} className={styles.logout} alt="avatar" />
          </div>
        </Link>
          </button>
        </div>
      </div>
      {showDialog && (
        <LogoutDialog
          onConfirm={handleConfirmLogout}
          onCancel={handleCancelLogout}
        />
      )}
    </nav>
  );
}

export default NavBar;
